import { BookList } from "../components/BooksList";
import { search, update, getAll } from "../BooksAPI";
import {useNavigate} from "react-router-dom";
import { useEffect, useState } from "react";

export const SearchPage = () => {
    const navigate = useNavigate();
    const [books, setBooks] = useState([]);
    const [myBooks, setMyBooks] = useState([]);

    useEffect(() => {
        fetchMyBooks()
      },[])

    const fetchMyBooks = async() => {
        await getAll().then((books) => setMyBooks(books))
      }

    const queryValueChange = async(query) => {
      await search(query, 20).then((booksResults) => {
        myBooks.forEach((book) => {
          let myBook = booksResults.find(e => e.title === book.title)
          // myBook = [...myBook]
        }) 
        setBooks(booksResults)
      })
    }

    const updateBook = async(book, shelf) => {
      await update(book, shelf)
    }

    return (
      <div className="search-books">
          <div className="search-books-bar">
            <a className="close-search"
               onClick={() => navigate(-1)}>
                Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                onChange ={(e) => queryValueChange(e.target.value)}
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <BookList title = {"Search Results: "} books = {books} onOptionSelected = {updateBook} />
        </div>
    );
}
