import { BookList } from "../components/BooksList";
import { getAll, update } from "../BooksAPI";
import { useEffect, useState } from "react";

export const HomePage = () => {
    const [books, setBooks] = useState([]);
    useEffect(() => {
        fetchAllBooks()
      },[])

    const fetchAllBooks = async() => {
        await getAll().then((books) => setBooks(books))
      }

    const updateBook = async(book, shelf) => {
       const results =  await update(book, shelf)
        // .then(() => getAll())
        // .then((books) => setBooks(books))
      }

    return (
      <div>
        <BookList title = {"Currently Reading"} books = {books.filter((book) => book?.shelf == "currentlyReading")} onOptionSelected = {updateBook} />
        <BookList title = {"Want to Read"} books = {books.filter((book) => book?.shelf == "wantToRead")} onOptionSelected = {updateBook} />
        <BookList title = {"Read"} books = {books.filter((book) => book?.shelf == "read")} onOptionSelected = {updateBook} />
      </div>
    );
}
