import { BookList } from "../components/BooksList";
import { search } from "../BooksAPI";
import { useState } from "react";
import {useNavigate} from "react-router-dom";

export const SearchPage = () => {
    const navigate = useNavigate();
    const [books, setBooks] = useState([]);
    const [queryValue, setQueryValue] = useState('');

    const queryValueChange = async(query) => {
      await search(query, 20).then((books) => setBooks(books))
    }


    return (
      <div className="search-books">
          <div className="search-books-bar">
            <a className="close-search"
               onClick={() => navigate(-1)}>
                Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                onChange ={(e) => queryValueChange(e.target.value)}
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <BookList title = {"Currently Reading"} books = {books.filter((book) => book?.shelf == "currentlyReading")} onOptionSelected = {updateBook} />

        </div>
    );
}
