import { useState } from "react";

export const BookList = ({title, books, onOptionSelected}) => {
  const [koko, setKoko] = useState([]);

    return books?.length ? (
        <div className="list-books-content">
             <div className="bookshelf">
                <h2 className="bookshelf-title">{title}</h2>
                <div className="bookshelf-books">
                  <ol className="books-grid">
                    {books.map((book) => (
                      <li>
                      <div className="book">
                        <div className="book-top">
                          <div
                            className="book-cover"
                            style={{
                              width: 128,
                              height: 188,
                              backgroundImage:
                                `url(${book?.imageLinks?.thumbnail})`,
                            }}
                          ></div>
                          <div className="book-shelf-changer">
                          <select defaultValue={book?.shelf ?? 'none'}
                           value = {koko}
                           onChange={e => {
                            setKoko(e.target.value)
                            onOptionSelected(book, e.target.value)
                          }}>
                             <option value="none" disabled> Move to... </option> 
                              <option value="currentlyReading"> Currently Reading </option>
                              <option value="wantToRead">Want to Read</option>
                              <option value="read">Read</option>
                              ko
                            </select>
                          </div>
                        </div>
                        <div className="book-title">{book?.title}</div>
                        <div className="book-authors">{book?.authors?.map((author) => (`${author}, `))}</div>
                      </div>
                    </li>
                     ))}

                  </ol>
                </div>
              </div>
        </div>
    ) : <div></div>;
};
